import React, { Component, useRef } from 'react';
import './App.css';
import NewTodo from './NewTodo';
import ToDo from './ToDo';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      todos : []
    };
    this.cancelB = this.cancelB.bind(this);
    this.add = this.add.bind(this);
    this.completeB = this.complete.bind(this);
  }

  componentDidMount(){
    var xhttp = new XMLHttpRequest();
    var key = "a1fd35-e5beef-3926ef-2138b5-62742e";
    xhttp.open("GET", "https://cse204.work/todos", true);
    xhttp.setRequestHeader("x-api-key", key);
    xhttp.send();
    if(this.readyState == 4 && this.status == 200){
      this.state.todos = JSON.parse(this.responseText);
    }
  }

  cancelB(){
    console.log("check");
    var xhttp = new XMLHttpRequest();
    var key = "a1fd35-e5beef-3926ef-2138b5-62742e";
    xhttp.open("DELETE", "https://cse204.work/todos/" + this.id, true);
    xhttp.setRequestHeader("Content-type", "application/json");
    xhttp.setRequestHeader("x-api-key", key);
    xhttp.send();
    //this.remove();
    var ind = this.state.todos.indexOf(this);
  }

  add() {
    var dataRaw = document.getElementById("input").value;
    var xhttp = new XMLHttpRequest();
    var key = "a1fd35-e5beef-3926ef-2138b5-62742e";
    var temp = new NewTodo();
    console.log(this);
    temp.text = dataRaw;
    console.log(this.id);
    //temp.id = ;
    this.setState(state => ({todos:[...state.todos, temp]}));
    xhttp.open("POST", "https://cse204.work/todos", true);
    xhttp.setRequestHeader("Content-type", "application/json");
    xhttp.setRequestHeader("x-api-key", key);
    var data = {
      text: temp.text
    }
    xhttp.send(JSON.stringify(data));
  }

  

  completeB(){
    var data = {
      "completed": true
  }
  var xhttp = new XMLHttpRequest();
    var key = "a1fd35-e5beef-3926ef-2138b5-62742e";
    
    xhttp.open("PUT", "https://cse204.work/todos/" + this.id, true);
    xhttp.setRequestHeader("Content-type", "application/json");
    xhttp.setRequestHeader("x-api-key", key);
    xhttp.send(JSON.stringify(data));
  }

  render() {
    return (
      <div id="mainSection">
        <h1>Josh & Michael's ToDo App</h1>
        {this.state.todos.map((todo) =>
          <NewTodo key={todo.id} text={todo.text} cancel={this.cancelB} complete={this.completeB}/>
          
        )}
        <ToDo add={this.add} />
      </div>
    );
  }
}

export default App;
